(function(){tinymce.PluginManager.requireLangPack('dailytube');tinymce.create('tinymce.plugins.DailyTubePlugin',{init:function(ed,url){ed.addCommand('mceDailyTube',function(){ed.windowManager.open({file:url+'/dialog.htm',width:400+parseInt(ed.getLang('dailytube.delta_width',0)),height:270+parseInt(ed.getLang('dailytube.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('dailytube',{title:'dailytube.desc',cmd:'mceDailyTube',image:url+'/img/dailytube.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('dailytube',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Dailymotion and Youtube tube plugin',author:'Claroline team',authorurl:'http://www.claroline.net',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('dailytube',tinymce.plugins.DailyTubePlugin)})();